import React, { useReducer } from "react";
import "./App.css";
import { encryptString } from "./machineLogic";
console.log(encryptString);
function App() {
  const reducer = (state: any, action: any) => {
    switch (action.type) {
      case "incrementShift":
        return { ...state, rotorShift: state.rotorShift + 1 };
      case "decrementShift":
        return { ...state, rotorShift: state.rotorShift - 1 };
      case "setRotorValue":
        return {
          ...state,
          rotorValues: state.rotorValues.map(
            //Iterate through rotors, return original value if not on value to be changed, else return specified value
            (oldValue: string, index: number) =>
              index == action.payload.rotorIndex
                ? action.payload.newValue
                : oldValue
          ),
        };
    }
  };

  const initialState = {
    rotorShift: 0,
    rotorValues: ["A", "B", "C"],
  };

  const [state, dispatch] = useReducer(reducer, initialState);

  /*
    Takes two strings(both permuations of the alphabet), and an index. 
    Finds the character at the given index in inputRow, then
    returns the index of that character in the outputRow.   
    Returns -1 when given invalid input
  */
  const rotorProcess = (inputRow: string, outputRow: string, index: number) =>
    outputRow.indexOf(inputRow[index]);
  /*
    Takes a number and a string and return the index of the other occurance of the character at that point
    e.g aabbcc and 0 would return 1
    I'm aware this isn't the fastest way to do it, but it keeps it concise and also has good automatic handling of 
    an index larger than the length of the string, if speed became an issue I would change this
  */
  const reflector = (reflector: string, index: number) => {
    const charAtIndex = reflector[index];
    const firstIndex = reflector.indexOf(charAtIndex);
    if (firstIndex == index) {
      return reflector.lastIndexOf(charAtIndex);
    } else {
      return firstIndex;
    }
  };

  /*
  Takes an index and a string of length 26(must be permutation of alphabet for our purposes)
  Returns string shifted by that index
  */
  const rotate = (string: string, index: number) =>
    string.length == 26 &&
    string.substring(index % 26) + string.substring(0, index % 26);

  return (
    <div className="App">
      <header className="App-header">
        <h1>Engima</h1>
        <h2>Rotor Shift: {state.rotorShift}</h2>
        <h2>Rotor Settings: {state.rotorSettings}</h2>
      </header>
    </div>
  );
}

export default App;
