import React, { useReducer, useState } from "react";
import "./App.css";
import { encryptString } from "./machineLogic";
import { rotors, reflector } from "./config";
import { Rotor } from "./components/rotor";
import { PlainRow } from "./components/row";
function App() {
  const reducer = (state: any, action: any) => {
    const rotate = (string: string, index: number) =>
      string.length == 26 &&
      string.substring(index % 26) + string.substring(0, index % 26);

    switch (action.type) {
      case "rotateRotor":
        return {
          ...state,
          rotors: state.machine.rotors.map(
            //Iterate through rotors, shift the input and output rotor of rotor with specified index
            (oldValue: any, index: number) =>
              index == action.payload.rotorIndex
                ? {
                    inputRotor: rotate(oldValue.inputRow, action.payload.shift),
                    outputRotor: rotate(
                      oldValue.outputRow,
                      action.payload.shift
                    ),
                  }
                : oldValue
          ),
        };
    }
  };

  const alphabet = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";

  const initialState = {
    machine: {
      rotors: rotors.map((rotorString: string) => {
        return { inputRow: rotorString, outputRow: alphabet, shift: 0 };
      }),
      reflector: reflector,
    },
    input: "I am an input",
  };

  const highlights: any = {
    input: { forwardPassLightUp: [0], backwardPassLightUp: [1] },
    output: { forwardPassLightUp: [2], backwardPassLightUp: [3] },
  };
  const [state, dispatch] = useReducer(reducer, initialState);
  console.log(state.machine);
  return (
    <div className="App">
      <button
        onClick={() =>
          dispatch({
            type: "rotateRotor",
            payload: { rotorIndex: 0, shift: 0 },
          })
        }
      >
        Shift
      </button>
      ;<div className="well">Enigma Simulator</div>
      <div className="well">
        <p>
          Configuration Code:
          <button className="btn btn-info">Reset</button>
        </p>
        <input type="range" className="slider" id="formControlRange"></input>
        <br></br>
        <input type="range" className="slider" id="formControlRange"></input>
        <br></br>
        <input type="range" className="slider" id="formControlRange"></input>
      </div>
      <div className="well">
        <div className="form-group">
          <label>
            <h2>Input</h2>
          </label>
          <input
            type="text"
            className="form-control bg-success"
            id="in"
            value=""
          ></input>
        </div>
        <table
          style={{
            borderLeft: "2px solid white !important",
            borderRight: "2px solid white !important",
          }}
        >
          <tbody>
            <PlainRow
              className="well"
              row={alphabet}
              // highlights={highlights}
            ></PlainRow>
            <Rotor
              rotor={state.machine.rotors[0]}
              className={`alert-danger`}
              highlights={highlights}
            ></Rotor>
            <Rotor
              rotor={state.machine.rotors[1]}
              className={`alert-warning`}
              highlights={highlights}
            ></Rotor>
            <Rotor
              rotor={state.machine.rotors[2]}
              className={`alert-success`}
              highlights={highlights}
            ></Rotor>
            <PlainRow
              className="well"
              row={reflector}
              // highlights={highlights}
            ></PlainRow>
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
