import React, { Fragment } from "react";
interface IProps {
  rotor: any;
  className: String;
  inputHighlight: string;
  ouputHighlight: string;
}
export const Rotor: React.FC<IProps> = ({
  rotor,
  className,
  inputHighlight,
  ouputHighlight,
}) => {
  const characterToElement = (
    borderClass: string,
    highlightIndex: number,
    highlightClass: string
  ) => (char: string, index: number) => {
    const highlighted = index === highlightIndex && highlightClass;
    //Class that adds a border once every two rotors, gives the impression everything is moving
    const bordered = (index + rotor.shift) % 2 === 0 && borderClass;
    <td
      className={`td ${className} ${highlighted} ${bordered}`}
    >
      {char}
    </td>;
  };
  return (
    <Fragment>
      <tr>{rotor.inputRow.split("").map(characterToElement("borderRight"),0, 'forwardPassLightUp')}</tr>
      <tr>{rotor.outputRow.split("").map(characterToElement("borderLeft"))}</tr>
    </Fragment>
  );
};
