import React, { useReducer, useState } from "react";
import "./App.css";
import { encryptString } from "./machineLogic";
import { rotors, reflector } from "./config";
import { stringify } from "querystring";
import { Rotor } from "./components/rotor";
function App() {
  const reducer = (state: any, action: any) => {
    const rotate = (string: string, index: number) =>
      string.length == 26 &&
      string.substring(index % 26) + string.substring(0, index % 26);

    switch (action.type) {
      case "rotateRotor":
        return {
          ...state,
          rotors: state.rotors.map(
            //Iterate through rotors, shift the input and output rotor of rotor with specified index
            (oldValue: any, index: number) =>
              index == action.payload.rotorIndex
                ? {
                    inputRotor: rotate(oldValue.inputRow, action.payload.shift),
                    outputRotor: rotate(
                      oldValue.outputRow,
                      action.payload.shift
                    ),
                  }
                : oldValue
          ),
        };
    }
  };

  const alphabet = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";

  const initialState = {
    machine: {
      rotors: rotors.map((rotorString: string) => {
        return { inputRow: rotorString, outputRow: alphabet, shift: 0 };
      }),
      reflector: reflector,
    },
    input: "I am an input",
  };
  console.log((string: string) => encryptString(string, state.machine));
  const [state, dispatch] = useReducer(reducer, initialState);
  return (
    <div className="App">
      <div className="well">Enigma Simulator</div>
      <div className="well">
        <table
          style={{
            borderLeft: "2px solid white !important",
            borderRight: "2px solid white !important",
          }}
        >
          <tbody>
            <tr id="input">
              <td className="td well forwardPassLightUp">A</td>
              <td className="td well backwardPassLightUp">B</td>
              <td className="td well">C</td>
              <td className="td well">D</td>
              <td className="td well">E</td>
              <td className="td well">F</td>
              <td className="td well">G</td>
              <td className="td well">H</td>
              <td className="td well">I</td>
              <td className="td well">J</td>
              <td className="td well">K</td>
              <td className="td well">L</td>
              <td className="td well">M</td>
              <td className="td well">N</td>
              <td className="td well">O</td>
              <td className="td well">P</td>
              <td className="td well">Q</td>
              <td className="td well">R</td>
              <td className="td well">S</td>
              <td className="td well">T</td>
              <td className="td well">U</td>
              <td className="td well">V</td>
              <td className="td well">W</td>
              <td className="td well">X</td>
              <td className="td well">Y</td>
              <td className="td well">Z</td>
            </tr>
            <Rotor
              rotor={state.machine.rotors[0]}
              className={`alert-danger`}
              inputHighlight={0}
              ouputHighlight={0}
            ></Rotor>
            <Rotor
              rotor={state.machine.rotors[1]}
              className={`alert-warning`}
              inputHighlight={0}
              ouputHighlight={0}
            ></Rotor>
            <Rotor
              rotor={state.machine.rotors[2]}
              className={`alert-success`}
              inputHighlight={0}
              ouputHighlight={0}
            ></Rotor>
            <tr id="reflector">
              <td className="td well">A</td>
              <td className="td well">B</td>
              <td className="td well">C</td>
              <td className="td well">D</td>
              <td className="td well">E</td>
              <td className="td well forwardPassLightUp">F</td>
              <td className="td well">G</td>
              <td className="td well">D</td>
              <td className="td well">I</td>
              <td className="td well">J</td>
              <td className="td well">K</td>
              <td className="td well">G</td>
              <td className="td well">M</td>
              <td className="td well">K</td>
              <td className="td well">M</td>
              <td className="td well">I</td>
              <td className="td well">E</td>
              <td className="td well">B</td>
              <td className="td well backwardPassLightUp">F</td>
              <td className="td well">T</td>
              <td className="td well">C</td>
              <td className="td well">V</td>
              <td className="td well">V</td>
              <td className="td well">J</td>
              <td className="td well">A</td>
              <td className="td well">T</td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
