import React, { Fragment } from "react";
interface IProps {
  rotor: any;
  className: String;
}
export const Rotor: React.FC<IProps> = ({ rotor, className }) => {
  const characterToElement = (borderClass:string)=>(char: string, index:number) => (
    <td className={`td ${className} forwardPassLightUp ${index%2==0 && borderClass}`}>{char}</td>
  );
  return (
    <Fragment>
      <tr>
        {rotor.inputRow.split("").map(characterToElement('borderRight'))}
      </tr>
      <tr>
        {rotor.outputRow.split("").map(characterToElement('borderLeft'))}
      </tr>
    </Fragment>
  );
};
