import React, { useReducer, useState } from "react";
import "./App.css";
import { encryptString } from "./machineLogic";
import { rotors, reflector } from "./config";
import { stringify } from "querystring";
console.log(encryptString);
function App() {
  const reducer = (state: any, action: any) => {
    const rotate = (string: string, index: number) =>
      string.length == 26 &&
      string.substring(index % 26) + string.substring(0, index % 26);

    switch (action.type) {
      case "rotateRotor":
        return {
          ...state,
          rotors: state.rotors.map(
            //Iterate through rotors, return original value if not on value to be changed, else return specified value
            (oldValue: string, index: number) =>
              index == action.payload.rotorIndex
                ? rotate(oldValue, action.payload.shift)
                : oldValue
          ),
        };
    }
  };

  const initialState = {
    machine: {
      rotors: rotors,
      reflector: reflector,
    },
    input: "I am an input",
  };

  const [state, dispatch] = useReducer(reducer, initialState);

  return (
    <div className="App">
      <header className="App-header">
        <h1>Engima</h1>
        <h2>Rotor Shift: {state.input}</h2>
        <h2>Rotor Shift: {state.rotorShift}</h2>
        <h2>Rotor Settings: {state.rotorSettings}</h2>
      </header>
    </div>
  );
}

export default App;
